import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import ImagePopup from './ImagePopup';
import "../pages/index.css";
import PopupWithForm from "./PopupWithForm";

function App() {
  return (
    <div>
      {/* ROOT */}
      <body className="root">
        {/* HEADER */}
        <Header />
        {/* CONTENT */}
        <Main />
        {/* FOOTER */}
        <Footer />
        {/* POPUPS */}
        <PopupWithForm title name />
      </body>
    </div>
  );
}

export default App;

function handleEditAvatarClick() {
  const popup = document.querySelector(".popup_edit-avatar").closest(".popup");
  const popupClose = popup.querySelector(".popup__close-btn");
  console.log("Update Avatar!");

  popup.classList.add("popup_opened");
  popupClose.onclick = function () {
    popup.classList.remove("popup_opened");
  };
}

function handleEditProfileClick() {
  const popup = document.querySelector(".popup_edit-profile").closest(".popup");
  const popupClose = popup.querySelector(".popup__close-btn");
  console.log("Update User Info!");

  popup.classList.add("popup_opened");
  popupClose.onclick = function () {
    popup.classList.remove("popup_opened");
  };
}

function handleAddPlaceClick() {
  const popup = document.querySelector(".popup_add-place").closest(".popup");
  const popupClose = popup.querySelector(".popup__close-btn");
  console.log("Add New Place!");

  popup.classList.add("popup_opened");
  popupClose.onclick = function () {
    popup.classList.remove("popup_opened");
  };
}
