import "../pages/index.css";

function Main(props) {
  return (
    <main className="main">
      {/* PROFILE */}
      <section className="profile">
        <div className="profile__avatar-area">
          <img src="#src" alt="#alt" className="profile__avatar" />
          <button
            type="button"
            className="profile__avatar-btn"
            aria-label="Редактировать аватар"
            onClick={handleEditAvatarClick}
          />
        </div>
        <div className="profile__info">
          <div className="profile__block-name">
            <h1 className="profile__name" id>
              Pavel Khokhlov
            </h1>
            <button
              type="button"
              className="button profile__edit-btn"
              aria-label="кнопка редактировать профиль"
              onClick={handleEditProfileClick}
            />
          </div>
          <p className="profile__job">Web developer</p>
        </div>
        <button
          type="button"
          className="button profile__add-btn"
          aria-label="кнопка добавить место"
          onClick={handleAddPlaceClick}
        />
      </section>
      {/* PLACES */}
      <ul className="places" />
    </main>
  );
}

export default Main;

function handleEditProfileClick() {
  const popup = document.querySelector(".popup_edit-profile").closest(".popup");
  const popupClose = popup.querySelector(".popup__close-btn");
  console.log("Update User Info!");

  popup.classList.add("popup_opened");
  popupClose.onclick = function () {
    popup.classList.remove("popup_opened");
  };
}

function handleAddPlaceClick() {
  const popup = document.querySelector(".popup_add-place").closest(".popup");
  const popupClose = popup.querySelector(".popup__close-btn");
  console.log("Add New Place!");

  popup.classList.add("popup_opened");
  popupClose.onclick = function () {
    popup.classList.remove("popup_opened");
  };
}
