// CLASS
export const scrollOff = "root_scroll-disabled";
export const popupOpened = "popup_opened";
export const placeLikeActive = "place__like-btn_active";
export const inputInvalidClass = "popup__input_invalid";
export const activeErrorClass = "popup__input-error_active";
export const inactiveButtonClass = "popup__save-btn_inactive";
export const disableDeleteBtn = "place__del-btn_disable"

// ELEMENTS
export const buttonEditProfile = document.querySelector(".profile__edit-btn");
export const nameProfile = document.querySelector(".profile__name");
export const jobProfile = document.querySelector(".profile__job");
export const nameInput = document.querySelector(".popup__input_name-profile");
export const jobInput = document.querySelector(".popup__input_job-profile");
export const buttonAvatar = document.querySelector(".profile__avatar-btn");
export const userAvatar = document.querySelector(".profile__avatar");
export const avatarLinkInput = document.querySelector(
  ".popup__input_avatar-link"
);
export const buttonAddPlace = document.querySelector(".profile__add-btn");
export const inputNamePlace = document.querySelector(
  ".popup__input_name-place"
);
export const inputLinkPlace = document.querySelector(
  ".popup__input_link-place"
);
export const inputElement = document.querySelector(".popup__input");
export const popupZoom = document.querySelector(".popup-zoom");
export const elementFormProfile = document.querySelector(".popup_edit-profile");
export const elementFormAvatar = document.querySelector(".popup_edit-avatar");

// SELECTORS
export const root = ".root";
export const placesList = ".places";
export const placeTemplate = ".place-template";
export const placeLikeBtn = ".place__like-btn";
export const placeDelBtn = ".place__del-btn";
export const placeImage = ".place__image";
export const placeTitle = ".place__title";
export const placeLikeCount = ".place__like-count";

export const popupElement = ".popup";
export const formSelector = ".popup__container";
export const popupCloseBtn = ".popup__close-btn";
export const popupZoomImg = ".popup-zoom";
export const zoomImage = ".popup-zoom__image";
export const zoomCaption = ".popup-zoom__caption";
export const formProfile = ".popup_edit-profile";
export const formAvatar = ".popup_edit-avatar";
export const formPlace = ".popup_add-place";
export const formConfirmDelPlace = ".popup_del-place";

export const inputSelector = ".popup__input";
export const submitButton = ".popup__save-btn";
