function PopupWithForm(props) {
  return (
    <section className="popup">
      <form
        className={`popup__container popup_${props.name}`}
        method="post"
        noValidate
      >
        <button
          type="button"
          className="button popup__close-btn"
          aria-label="Вернуться на страницу"
        />
        <h2 className="popup__title">${props.title}</h2>

        <button
          type="submit"
          className="popup__save-btn"
          aria-label="Сохранить новый профиль"
        >
          Сохранить
        </button>
      </form>
    </section>
  );
}

export default PopupWithForm;

ReactDOM.render(
  <>
    <PopupWithForm title="Редактировать профиль" name="popup_edit-profile" />
    <PopupWithForm title="Обновить аватар" name="popup_edit-avatar" />
    <PopupWithForm title="Новое место" name="popup_add-place" />
  </>,
  document.querySelector(".main")
);

