import React from "react";

const ImagePopup = (props) => {
  return (
    <section
      className={
        props.secectedCard ? "popup popup-zoom popup_opened" : "popup popup-zoom"
      }
      onClick={props.onClose}
    >
      <figure
        className="popup-zoom__container"
        onClick={(e) => e.stopPropagation()}
      >
        <button
          type="button"
          className="button popup__close-btn"
          aria-label="Вернуться на страницу"
          onClick={props.onClose}
        />
        <img
          src={props.selectedCardard.link}
          alt={props.card.name}
          className="popup-zoom__image"
        />
        <figcaption className="popup-zoom__caption">
          {props.card.name}
        </figcaption>
      </figure>
    </section>
  );
};

export default ImagePopup;
