  popup.classList.add("popup_opened");
  popupClose.onclick = function () {
    popup.classList.remove("popup_opened");
  };
}

function handleEditProfileClick() {
  const popup = document.querySelector(".popup_edit-profile").closest(".popup");
  const popupClose = popup.querySelector(".popup__close-btn");
  console.log("Update User Info!");

  popup.classList.add("popup_opened");
  popupClose.onclick = function () {
    popup.classList.remove("popup_opened");
  };
}

function handleAddPlaceClick() {
  const popup = document.querySelector(".popup_add-place").closest(".popup");
  const popupClose = popup.querySelector(".popup__close-btn");
  console.log("Add New Place!");

  popup.classList.add("popup_opened");
  popupClose.onclick = function () {
    popup.classList.remove("popup_opened");
  };
}
